package http.impl;

import http.Interfaces.ICookie;

public class Cookie implements ICookie {
	private String name;
	private String value;
	
	public Cookie(String n, String v) {
		this.name = n;
		this.value = v;
	}

	@Override
	public String getName() {
		// TODO Auto-generated method stub
		return this.name;
	}

	@Override
	public String getValue() {
		// TODO Auto-generated method stub
		return this.value;
	}

}
